import React, { useState } from 'react';

import { backendAPIAxios } from '../../../utils/http';

import { ICrypto } from '../../../models/crypto';

import HistoricPricesView from './HistoricPrices.view';

interface Props {
  readonly prices: (value: ICrypto) => void;
}

const HistoricPrices: React.FC<Props> = (props: React.PropsWithChildren<Props>) => {

  const [ pricesState, setPricesState ] = useState<ICrypto | null>(null);

  const pricesChangeHandler = (value: ICrypto | null) => setPricesState(() => value);

  backendAPIAxios.get(`/prices`)
  .then(() => {
    props.prices(pricesState!);
  });

  return (
    <HistoricPricesView
      prices={pricesState}
      pricesChangeHandler={pricesChangeHandler}
    >{props.children}</HistoricPricesView>
  );
};

HistoricPrices.displayName = 'HistoricPrices';
HistoricPrices.defaultProps = {};

export default HistoricPrices;
