import React from 'react';
import { useTranslation } from 'react-i18next';

import { ICrypto } from '../../../models/crypto';

import CSvg from '../../ui/CSvg/CSvg';


import classes from './Prices.module.scss';

interface Props {
  readonly prices: ICrypto | null;
  readonly pricesChangeHandler: (value: ICrypto | null) => void;
  readonly changeCurrencyHandler: any;
}

const PricesView: React.FC<Props> = (props: React.PropsWithChildren<Props>) => {
  const { t } = useTranslation();

  return (
    <div className={classes['prices']}>
      <div className={classes['container']}>
        <h1 className={classes['container__header']}>{t('prices.header')}</h1>
        <div className={classes['innerContainer']}>
          <span className={classes['innerContainer__text']}>{t('prices.currency.btc')} {props.prices?.BTCUSD}</span>
          <a className={classes['innerContainer__text']} href={'/' + props.changeCurrencyHandler.BTCUSD}>{t('prices.text.btc')}</a>
          <CSvg 
            className={classes['innerContainer__icon']}
            name='BTCUSD'
          />
        </div>
        <div className={classes['innerContainer']}>
          <span className={classes['innerContainer__text']}>{t('prices.currency.eth')} {props.prices?.ETHUSD}</span>
          <a className={classes['innerContainer__text']} href={'/' + props.changeCurrencyHandler.ETHUSD}>{t('prices.text.eth')}</a>
          <CSvg 
            className={classes['innerContainer__icon']}
            name='ETHUSD'
          />
        </div>
        <div className={classes['innerContainer']}>
          <span className={classes['innerContainer__text']}>{t('prices.currency.ltc')} {props.prices?.LTCUSD}</span>
          <a className={classes['innerContainer__text']} href={'/' + props.changeCurrencyHandler.LTCUSD}>{t('prices.text.ltc')}</a>
          <CSvg 
            className={classes['innerContainer__icon']}
            name='LTCUSD'
          />
        </div>
      </div>
    </div>
  );
};

PricesView.displayName = 'PricesView';
PricesView.defaultProps = {};

export default PricesView;
